import React from "react"
import { Typography } from "@material-ui/core"
import { Button } from "@material-ui/core"
import Card from "../card"
import ToggleButton from '@material-ui/lab/ToggleButton';
import ToggleButtonGroup from '@material-ui/lab/ToggleButtonGroup';
import { Paper } from "@material-ui/core"
import { makeStyles } from "@material-ui/core/styles"
import { Grid } from "@material-ui/core"
import axios from 'axios';
import "../settingCard/settingCard.css"

export default function SettingCard(props){
    let questionCount = 1;
    let questionArray = [];

    const [startSettings, setStartSettings] = React.useState(true);
    const handleSettings = () => {
      setStartSettings(false);
      setStartGame(true);
      getQuestions();
    }

    const [startGame, setStartGame] = React.useState(false);

    const [number, setNumber] = React.useState(0);
    const handleChange = (e, newNumber) => {
        setNumber(newNumber);
    }
    const getQuestions = () => {
        let count = 0;
        let questions = [];
        axios.get('https://strangers-or-not.firebaseio.com/.json')
            .then(res => {
                for (const [key, value] of Object.entries(res.data)){
                    while (count < number){
                        questions.push(value[Math.floor((Math.random() * value.length))]);
                        count++;
                    }
                    count = 0;
                }
            })
            .then(() => {
                questionArray = questions;
            })
    }

    const questionLevelLabel = () => {
        if (questionCount <= number){
            return "Level 1: Perception";
        }
        else if (questionCount <= number*2){
            return "Level 2: Connection";
        }
        else if (questionCount <= number*3){
            return "Level 3: Reflection"
        }
    }

    let questionComponents = questionArray.map((question) => {
        return (<Grid container>
        <Grid item sm={2} md={2} lg={2}></Grid>
        <Grid item xs={12} sm={8} md={8} lg={8} className="gameStyling">
            <Typography variant="h5" className="numberStyling">Card {questionCount}/{number*3}</Typography>
            <Card title={questionLevelLabel} question={question} brand="Strangers or Not"/>
            <Button variant="outlined" className="prevButton prevNext">Previous</Button>
            <Button variant="outlined" className="nextButton prevNext">Next</Button>
        </Grid>
        <Grid item sm={2} md={2} lg={2}></Grid>
    </Grid>)
    });


    return(
        <div>
            {startSettings == true ? 
            <Paper elevation={7} className="paperSetting">
                <Grid container>
                    <Grid item xs={12} sm={12} md={12} lg={12}>
                        <Typography align="center" className="cardLevel" variant="h4">How many cards do you <br/>want to play per level?</Typography>
                    </Grid>
                    <Grid item xs={12} sm={12} md={12} lg={12}>
                        <Grid container>
                        <Grid item xs={1} sm={1} md={1} lg={1}></Grid>
                        <Grid item xs={10} sm={10} md={10} lg={10}>
                            <ToggleButtonGroup value={number} exclusive onChange={handleChange} className="groupSpace">
                                <ToggleButton disabled={number === 3 ? true : false} value={3} className={number === 3 ? "disabledButton" : "buttonGroup"}>3</ToggleButton>
                                <ToggleButton disabled={number === 5 ? true : false} value={5} className={number === 5 ? "disabledButton" : "buttonGroup"}>5</ToggleButton>
                                <ToggleButton disabled={number === 7 ? true : false} value={7} className={number === 7 ? "disabledButton" : "buttonGroup"}>7</ToggleButton>
                                <ToggleButton disabled={number === 9 ? true : false} value={9} className={number === 9 ? "disabledButton" : "buttonGroup"}>9</ToggleButton>
                                <ToggleButton disabled={number === 11 ? true : false} value={11} className={number === 11 ? "disabledButton" : "buttonGroup"}>11</ToggleButton>
                                <ToggleButton disabled={number === 13 ? true : false} value={13} className={number === 13 ? "disabledButton" : "buttonGroup"}>13</ToggleButton>
                                <ToggleButton disabled={number === 15 ? true : false} value={15} className={number === 15 ? "disabledButton" : "buttonGroup"}>15</ToggleButton>
                            </ToggleButtonGroup>
                        </Grid>
                    </Grid>
                    <Grid item xs={1} sm={1} md={1} lg={1}></Grid>                    
                </Grid>
                    <Grid item xs={12} sm={12} md={12} lg={12}>
                        <Grid container>
                            <Grid item xs={3} sm={3} md={3} lg={3}></Grid>
                            <Grid item xs={7} sm={7} md={7} lg={7}>
                                <Button variant="contained" size="large" className="startButton" onClick={handleSettings}>Start Game</Button>
                            </Grid>
                            <Grid item xs={2} sm={2} md={2} lg={2}></Grid>
                        </Grid>
                    </Grid>
                </Grid>
            </Paper>
            : null}
            {startGame == false ? null : questionComponents}
        </div>
    )
};
