import React from "react"
import { Typography } from "@material-ui/core"
import { Button } from "@material-ui/core"
import ToggleButton from '@material-ui/lab/ToggleButton';
import ToggleButtonGroup from '@material-ui/lab/ToggleButtonGroup';
import { Paper } from "@material-ui/core"
import { makeStyles } from "@material-ui/core/styles"
import { Grid } from "@material-ui/core"
import "../settingCard/settingCard.css"

const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      '& > *': {
        margin: theme.spacing(1),
      },
    },
  }));

export default function SettingCard(){
    const classes = useStyles();

    const [number, setNumber] = React.useState(0);
    const handleChange = (e, newNumber) => {
        alert(newNumber);
        setNumber(newNumber);
    }

    return(
        <div className={classes.root}>
            <Paper elevation={7} className="paper">
                <Grid container>
                    <Grid item xs={12} sm={12} md={12} lg={12}>
                        <Typography align="center" className="cardLevel" variant="h4">How many cards do you <br/>want to play per level?</Typography>
                    </Grid>
                    <Grid item xs={12} sm={12} md={12} lg={12}>
                        <Grid container xs={12} sm={12} md={12} lg={12}>
                        <Grid item xs={1} sm={1} md={1} lg={1}></Grid>
                        <Grid item xs={1} sm={1} md={1} lg={1}>
                            <ToggleButtonGroup value={number} exclusive onChange={handleChange} className="groupSpace">
                                <ToggleButton value={3} className="buttonGroup">Three</ToggleButton>
                                <ToggleButton value={5} className="buttonGroup">Five</ToggleButton>
                                <ToggleButton value={7} className="buttonGroup">Seven</ToggleButton>
                                <ToggleButton value={9} className="buttonGroup">Nine</ToggleButton>
                                <ToggleButton value={11} className="buttonGroup">Eleven</ToggleButton>
                                <ToggleButton value={13} className="buttonGroup">Thirteen</ToggleButton>
                                <ToggleButton value={15} className="buttonGroup">Fifteen</ToggleButton>
                            </ToggleButtonGroup>
                        </Grid>
                    </Grid>
                    <Grid item xs={1} sm={1} md={1} lg={1}></Grid>                    
                </Grid>
                    <Grid item xs={12} sm={12} md={12} lg={12}>
                        <Grid container>
                            <Grid item xs={3} sm={3} md={3} lg={3}></Grid>
                            <Grid item xs={7} sm={7} md={7} lg={7}>
                                <Button variant="contained" size="large" className="startButton">Start Game</Button>
                            </Grid>
                            <Grid item xs={2} sm={2} md={2} lg={2}></Grid>
                        </Grid>
                    </Grid>
                </Grid>
            </Paper>

        </div>
    )
};
