import React, { useState, useEffect } from "react"
import { Typography } from "@material-ui/core"
import Card  from "../../../components/card/card"
import { Grid } from "@material-ui/core"
import { Button } from "@material-ui/core"
import "../../game/liveplay/liveplay.css"
import axios from 'axios';


export default function LivePlay(props){
    let number = props.numberPerRound;
    let [questionArray, setQuestionArray] = React.useState([])
    let [currentId, setCurrentId] = React.useState(0);
    let [seeFinalCard, setSeeFinalCard] = React.useState(false);
    function seeHandler() {
        setSeeFinalCard(!seeFinalCard);
        setCurrentId(currentId + 1);
    }

    useEffect(() => {
        let questions = [];
        let count = 0;
        axios.get('https://strangers-or-not.firebaseio.com/.json')
            .then(res => {
                let tempSet = new Set();
                for (const [key, value] of Object.entries(res.data)){
                    let tempArr = [];
                    while (count < number){
                        let card_value = value[Math.floor((Math.random() * value.length))];
                        if (key === "level1"){
                            if(!tempArr.includes(card_value)){
                                tempArr.push(card_value);
                                questions.push(["Level 1: Perception", card_value]);
                                count++;
                            }
                        }
                        else if (key === "level2"){
                            if(!tempArr.includes(card_value)){
                                tempArr.push(card_value);
                                questions.push(["Level 2: Connection", card_value]);
                                count++;
                            }
                        }
                        else if (key === "level3"){
                            if(!tempArr.includes(card_value)){
                                tempArr.push(card_value);
                                questions.push(["Level 3: Reflection", card_value]);
                                count++;
                            }
                        }
                    }
                    count = 0;
                }
                console.log(questions);
                setQuestionArray(questions);
            })
            .catch(err => {
                console.log(err);
            })
    }, [])

    function handlePrevious() {
        setCurrentId(currentId - 1);
    }

    function handleNext() {
        setCurrentId(currentId + 1);
    }

    return (
        <div>
            {questionArray.length > 0 && seeFinalCard == false ? 
            <Grid container key={currentId}>
                <Grid item sm={2} md={2} lg={2}></Grid>
                <Grid item xs={12} sm={8} md={8} lg={8} className="gameStyling">
                    <Typography variant="h5" className="numberStyling">Card {currentId+1} / {number*3}</Typography>
                    <Card title={questionArray[currentId][0]} question={questionArray[currentId][1]} brand="Strangers or Not"/>
                    <Button disabled={currentId < 1} 
                            variant="outlined" 
                            className="prevButton prevNext" 
                            onClick={handlePrevious}>Previous</Button>

                    <Button disabled={currentId >= questionArray.length-1} 
                            variant="outlined"
                            className="nextButton prevNext" 
                            onClick={currentId+1 == number*3 ? seeHandler : handleNext}>
                            {currentId+1 == number*3 ? 'Final Card' : 'Next'}</Button>
                </Grid>
                <Grid item sm={2} md={2} lg={2}></Grid>
            </Grid>
        : "Loading..."}
        {seeFinalCard == true ?
            <Grid container key={currentId}>
                <Grid item sm={2} md={2} lg={2}></Grid>
                <Grid item xs={12} sm={8} md={8} lg={8} className="gameStyling">
                    <Card title="The Final Card" question="Each player send a message to the other. Open only once the game has concluded." brand="Strangers or Not"/>
                    <Button disabled={currentId < 1} 
                            variant="outlined" 
                            className="prevButton prevNext" 
                            onClick={handlePrevious}>Previous</Button>

                    <Button disabled
                            variant="outlined"
                            className="nextButton prevNext" 
                            onClick={currentId+1 == number*3 ? seeHandler : handleNext}>
                            {currentId+1 == number*3 ? 'Final Card' : 'Next'}</Button>
                </Grid>
                <Grid item sm={2} md={2} lg={2}></Grid>
            </Grid>
        : null }
        </div>
    )
}
